const actions = {

    SUBMIT_ANSWER  : 'SUBMIT_THE_ANSWER',
    SKIP_ANSWER  : 'SKIP_THE_ANSWER',


submittheAnswer:(QuesNo,Answer_selected='')=>(
    {
        type:'SUBMIT_ANSWER',
        payload:[QuesNo,Answer_selected]

    }),

    skiptheAnswer:(Ques_No)=>(
        {
            type:'SKIP_ANSWER'
        })

};
export default actions;
