	setIsActive(true);
	setIsPaused(false);
};

React.useEffect(() => {
    let interval = null;
	if ( isActive && !isPaused) {
	interval = setInterval(() => {
		setTime((time) => time + 10);
	}, 10);
	} else {
    clearInterval(interval);
    handleStart()
	}
	return () => {
	clearInterval(interval);
	};
}, []);


if (time>5000){ 
    setIsActive(false)

}

const handlePauseResume = () => {
	setIsPaused(!isPaused);
};


const handleReset = () => {
	setIsActive(false);
	setTime(0);
};

return (
	<div className="stop-watch">
	<Timer time={time} />
	</div>
);
}

export default StopWatch;
