        {





        questions.map(question=>{
        const ans=question.answerOptions.find(optiona=>optiona['isCorrect']===true).option
        console.log(ans)
            return (<Col>
            <Card style={{textAlign:'center',borderRadius:'22px',widht:'15vw',height:'10vh',marginLeft:'2vw',marginRight:'2vw',marginTop:'5vh',marginBottom:'5vh',backgroundColor:' #00ff00'}}>
                <Card style={{backgroundColor:'white'}}>Question:{question.QuesNo}</Card>
                <Card>{ansARR[question.QuesNo-1]===ans?'right':'wrong'}</Card>
            </Card>
            </Col>)
})}
        </Row>
        <Card>
        <h1>Score:{score}</h1>
        </Card>

        </Card>





    )



}

export default Table;
