// import Header from './Components/Header'
import { useState,useEffect} from "react";
import { useDispatch, useSelector } from "react-redux";
import questions from './Questions'
import {Row,Col} from 'react-bootstrap'
import { Divider } from 'antd';
import { Card } from 'antd';
import { Button, Radio,Badge,Avatar } from 'antd';
import Countdown from 'react-countdown';
import StopWatch from './stopwatch'
import AnswersAction from '../redux/Answers/actions'
function Screen() {
  const{submittheAnswer,skiptheAnswer}=AnswersAction
  const [selected_option,set_selected_option]=useState('')
  const [currentQuestion, setCurrentQuestion] = useState(0);
	const [showScore, setShowScore] = useState(false);
  const [score, setScore] = useState(0);
  const[attempts,setattempts]=useState([0,0,0,0])
  const dispatch = useDispatch();


// var i = setInterval(function() {
//  console.log('hskskhksh')
// }, 500);
// if(showScore)(function(){
//   clearInterval(i);
// });
  // useInterval(handleAnswerOptionClick(false), 100000, 5);




  const handleclickoption = (option) => {
		set_selected_option(option)
  };


// var Countdown=(<h1>loding</h1>)
//   useEffect(()=>{

//   },[currentQuestion])



  const handlesubmit = () => {
    const arr=attempts
    arr[currentQuestion]=selected_option
    setattempts(arr)
    handleAnswerOptionClick(true)
    set_selected_option('')
    dispatch(submittheAnswer(currentQuestion,selected_option))
  };


  const handleskip = () => {
    const arr=attempts
    arr[currentQuestion]='NA'
    setattempts(arr)
    handleAnswerOptionClick(true)
    set_selected_option('')
};











  console.log(attempts,'attemptsksjksks')


  const handleAnswerOptionClick = () => {
		const nextQuestion = currentQuestion + 1;
		if (nextQuestion < questions.length) {
			setCurrentQuestion(nextQuestion);
		} else {
			setShowScore(true);
		}
  };






  return (

		<div className='app'>








<div>
{!showScore?
<div>
      <Row>


      <Col span={6}>

				<>
        <Divider>
					<div className='question-section'>

						<div className='question-count'>
            <Card style={{marginBottom:'2vh',borderRadius:'25px',textAlign:'center',marginTop:'3vh',marginLeft:'3vh',color:'white',backgroundColor:'#660066',width:'10vw'}}>
							<span>Question {currentQuestion + 1}</span>/{questions.length}
              </Card>
						</div>
						<Card style={{width:'60vw',marginBottom:'2vh',borderRadius:'25px',textAlign:'left',marginLeft:'3vh',color:'white',backgroundColor:'#8600b3'}}>

              <div className='question-text'>{questions[currentQuestion].questionText}</div></Card>
					</div>
         </Divider>
					<div className='answer-section'>
          <Row >

						{questions[currentQuestion].answerOptions.map((answerOption) => (

                <Col md={12}>
                  <Card style={{marginBottom:'3vh',marginLeft:'3vh'}}>
                    <Button style={{marginBottom:'3vh',marginRight:'1vh',backgroundColor:'#6600cc'}} onClick={() => handleAnswerOptionClick(answerOption.isCorrect)}>{answerOption.option}</Button>

                    {answerOption.option===selected_option?<Button style={{borderRadius:'25px',backgroundColor:'green'}} type="primary" bordered='false' onClick={() => handleclickoption(answerOption.option)} shape="round" >{answerOption.answerText}</Button> :<Button style={{borderRadius:'25px',backgroundColor:'#1a8cff'}} type="primary" bordered='false' onClick={() => handleclickoption(answerOption.option)} shape="round"  >
                  {answerOption.answerText}
        </Button>
}

							{/* <button onClick={() => handleAnswerOptionClick(answerOption.isCorrect)}>{answerOption.answerText}</button> */}

              </Card>
              </Col>

						))}

             </Row>

					</div>
				</>
			)

      </Col>

      <Col span={8} >{showScore?<StopWatch stopIT={true} currentQues={currentQuestion}/>:<StopWatch stopIT={false} currentQues={currentQuestion}/>}</Col>




      </Row>


      <Row>
        <Col span={4} style={{marginLeft:'20vw'}}>
          <Button style={{width:'13vw',marginLeft:'16vw',borderRadius:'22px'}} onClick={handlesubmit}>submit</Button>



          <Button style={{width:'10vw',marginLeft:'2vw',borderRadius:'22px'}} onClick={handleskip}>Skip</Button>


        </Col>

      </Row></div>:
      <Row>Table to show</Row>}

      </div>





		</div>)

}

export default Screen;
